class Solution(object):

    def maximalRectangle(self, matrix):
        """
        :type matrix: List[List[str]]
        :rtype: int
        """
        if not matrix:
            return 0
        lr = len(matrix)
        lc = len(matrix[0])
        area = 0
        for i in range(lr):
            left = 0
            for j in range(lc):
                v = matrix[i][j]
                matrix[i][j] = []
                if v == '1':
                    left += 1
                    up = matrix[i-1][j] if i > 0 else []
                    mm = [area]
                    pp = 1
                    for l, c in up:
                        if l <= left:
                            matrix[i][j].append((l, c+1))
                            mm.append(l * (c+1))
                            pp = 0 if l == left else pp
                        else:
                            matrix[i][j].append((left, c+1))
                            mm.append(left * (c+1))
                            pp = 0
                    if pp:
                        matrix[i][j].append((left, 1))
                        mm.append(left)
                    area = max(mm)
                else:
                    left = 0
        print(area)
        return area



